frappe.ui.form.on('Device', {
  setup (frm) {
    frm.set_query("detected_by", function() {
      return {
       filters: [
         ["Device","is_scanner", "=", 1]
       ]
      };
    });  
  },    
  onload(frm) {
    // your code here
    show_image(frm);
  },
  refresh(frm) {
    //var map = frm.get_field("location").map;
    //var latlng = L.latLng({'lat': 43.537621, 'lng': -5.659692});
    //var marker = L.marker(latlng);
    //map.flyTo(latlng, map.getZoom());
    //marker.addTo(map);
    //marker.bindPopup(frm.doc.name).openPopup();  
  },
  media_library (frm) {
    show_image(frm);  
  },
  is_located (frm) {
    if (frm.doc.is_located === 0) {
      frm.doc.rssi = null;
      frm.doc.training_place = null;
      frm.doc.detected_by = null;
    }      
  }
});
function show_image (frm) {
  if (frm.doc.media_library) {
    frappe.call({
      method: 'atvirtual.atvirtual.custom.get_image',
        args: {
	      slide: frm.doc.media_library
        },
        callback: (r) => {
          //console.log(r.message);
          if (r.message.length >0) {
            var rtemplate = "";
            console.log(r.message);
            for (var j in r.message) {
              //rtemplate += '<a href="' + r.message[j].image + '" target="_blank"><img style="border: 1px solid #4682b4; float: left; margin: 1px; padding: 1px;" height="120px" src="' + r.message[j].image + '" /></a>';  
              rtemplate += '<div class="card"><a class="card-text" href="' + r.message[j].image + '" target="_blank"><img style="border: 1px solid #4682b4; border-radius: 9px; float: left; margin: 1px; padding: 1px;" height="120px" title="' + r.message[j].heading + '" src="' + r.message[j].image + '" /></a></div>'; 
            }
            //var template = '<div class="container-fluid"><h4>' + frm.doc.media_library + '</h4>' + rtemplate + '</div>';
            var template = '<div class="container-fluid"><h4>' + frm.doc.media_library + '</h4><div class="card-columns">' + rtemplate + '</div></div>';
            //console.log(template);
            frm.set_df_property('html_image', 'options', frappe.render_template(template));
            frm.refresh_field('html_image');
          }
        }
    });    
  } else {
    //console.log(frm.doc.media_library);
    var noimage = '<div class="container-fluid"><h4>No Image Gallery</h4></div>';
    frm.set_df_property('html_image', 'options', frappe.render_template(noimage));
    frm.refresh_field('html_image');
  }
}

