  send_now: function(frm, cdt, cdn){
	var d = locals[cdt][cdn];
	//frappe.msgprint("Message " + d.sch_message);
	var msg = d.sch_message;
	//console.log(msg);
	if (d.sent === 0) {
	  frappe.call({
        method: 'atvirtual.atvirtual.custom.submit_pibimessage',
        args: {
          doc: msg
        },
        callback (r) {
          console.log(r.exec);  
        }
      });
	} else {
