frappe.ui.form.on('Standard Message', {
	refresh(frm) {
	  // your code here
	  json_string(frm);
	},
	std_message(frm) {
	  json_string(frm);    
	},
	type(frm) {
	  json_string(frm);    
	}
});
function json_string (frm) {
  var json_message = '';
  var order = '';
  if (frm.doc.std_message !== '' && frm.doc.type !== '') {
    if (frm.doc.order) {
	  order = frm.doc.order;
	}
	json_message = '{"type":"' + frm.doc.type.toLowerCase() +
	  ',"message":"' + frm.doc.std_message.replace(',','') +
	  '"}';
	  frm.doc.json_message = json_message;
    //console.log(json_message);
  }
  frm.refresh_field('json_message');
}

